  "method": "tools/call",
  "params": {
    "name": "code_complexity",
    "arguments": {
      "code": "function processUser(user) {
  if (!user) return null;
  if (user.age < 18) {
    return 'minor';
  } else if (user.age > 65) {
    return 'senior';
  }
  for (let i = 0; i < user.roles.length; i++) {
    if (user.roles[i] === 'admin') {
      return 'admin';
    }
  }
  return 'regular';
}",
